import React, { useState } from "react";
import "./App.css";
import "antd/dist/antd.css";
import { Form, Radio, Button, Descriptions } from "antd";
import { Input } from "antd";
import validatejs from "validate.js";
import { validate } from "graphql";

interface Props {
  name: string;
  code: string;
  title: string;
  description: string;
  flat: boolean;
  threshold: boolean;
  flexible: boolean;
  position: number;
  reportOverride: boolean;
  workflowStatus: number;
  onSubmit: Function;
  handleFormChange: Function;
}

// var constraints = {
//   name: {
//     presence: true,
//     exclusion: {
//       within: ["nicklas"],
//       message: "'%{value}' is not allowed"
//     }
//   }
// };

export const FormApp: React.FC<Props> = ({
  name,
  code,
  title,
  description,
  flat,
  threshold,
  flexible,
  handleFormChange,
  onSubmit
}) => {
  var pattern = /\d{5}(-\d{4})?/;

  const [validation, setValidation] = useState({
    name: {
      presence: true
    },

    code: {
      presence: true,
      format: pattern,
      onlyInteger: false
    },

    title: { presence: true, minimum: 5 },

    description: { presence: true, Mminimum: 10 }
  });

  const [errors, setErrors] = useState({
    // name: name,
    // code: code,
    // title: title,
    // description: description,
    // flat: flat,
    // flexible: flexible,
    // threshold: threshold
  });

  const validateForm = () => {
    setErrors(
      validatejs.validate(
        {
          name: name,
          code: code,
          title: title,
          description: description,
          flat: flat,
          flexible: flexible,
          threshold: threshold
        },
        validation
      )
    );
  };
  return (
    <Form>
      <Form.Item
        label="Name: "
        validateStatus={errors["name"] ? "error" : ""}
        help="Inserisci un nome"
      >
        <Input
          name="name"
          value={name}
          onChange={(event: any) => {
            validateForm();
            handleFormChange("name", event.target.value);
          }}
        ></Input>
      </Form.Item>

      <br></br>
      <Form.Item
        label="Code: "
        validateStatus={errors["code"] ? "error" : ""}
        help="Ricorda di inserire un codice che non sia unicamente numerico"
      >
        <Input
          name="code"
          value={code}
          onChange={(event: any) => {
            validateForm();
            handleFormChange("code", event.target.value);
          }}
        ></Input>
      </Form.Item>

      <br></br>
      <Form.Item
        label="Title: "
        validateStatus={errors["title"] ? "error" : ""}
        help="Inserisci un titolo che non abbia una parola con meno di 5 lettere"
      >
        <Input
          name="title"
          value={title}
          onChange={(event: any) => {
            validateForm();
            handleFormChange("title", event.target.value);
          }}
        ></Input>
      </Form.Item>

      <br></br>
      <Form.Item
        label="Description: "
        validateStatus={errors["description"] ? "error" : ""}
        help="messaggio"
      >
        <Input
          name="description"
          value={description}
          onChange={(event: any) => {
            validateForm();
            handleFormChange("description", event.target.value);
          }}
        ></Input>
      </Form.Item>

      <br></br>
      <Form.Item label="Flat: " validateStatus="" help="messaggio">
        <Radio
          value={flat}
          name="flat"
          defaultChecked={false}
          onChange={(event: any) => {
            handleFormChange("flat", event.target.checked);
          }}
        ></Radio>
      </Form.Item>

      <br></br>
      <Form.Item label="threshold: " validateStatus="" help="messaggio">
        <Radio
          name="threshold"
          defaultChecked={false}
          value={threshold}
          onChange={(event: any) => {
            handleFormChange("threshold", event.target.checked);
          }}
        ></Radio>
      </Form.Item>

      <br></br>
      <Form.Item label="threshold: " validateStatus="" help="messaggio">
        <Radio
          name="flexible"
          value={flexible}
          defaultChecked={false}
          onChange={(event: any) => {
            handleFormChange("flexible", event.target.checked);
          }}
        ></Radio>
      </Form.Item>

      <br></br>
      <br></br>
      <Button
        type="primary"
        onClick={(e: any) => {
          validateForm();
          onSubmit();
        }}
      >
        Aggiungi
      </Button>
    </Form>
  );
};
